import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";

const Data = (props) => {
  const [latestBlock, setlatestBlock] = useState(null);

    const testGetFun = () => {
      let url =
        "https://cors-anywhere.herokuapp.com/https://blockchain.info/latestblock";
      return async () => {
        fetch(url, {
          method: "GET",
          headers: {
            "Access-Control-Allow-Headers":
              "X-API-KEY, Origin, X-Requested-With, Content-Type, Accept, Access-Control-Request-Method,Access-Control-Request-Headers, Authorization",
            "Content-Type": "application/json",
          },
        })
          .then((response) => response.json())
          .then((data) => {


              console.log(data)
              let x = new Date(0);
            let z = x.setUTCSeconds(data["time"]);
              let y = `${x.getDate()}-${x.getMonth()}-${x.getFullYear()}`
            const dObject = {
                height:data["height"],
                hash:data["hash"],
                time:y,
                block_index:data["block_index"],
                total_transaction:data["txIndexes"].length
            }
            console.log(dObject);
              setlatestBlock(dObject);
            });
      };
    };

  const getLatestBlock = () => {
    return async () => {
      // let url = "https://cors-anywhere.herokuapp.com/https://blockchain.info/latestblock"
      let url =
        "https://cors-anywhere.herokuapp.com/https://blockchain.info/latestblock";
      let res = await axios.get(url, {
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8",
          "Access-Control-Allow-Headers":
            "X-API-KEY, Origin, X-Requested-With, Content-Type, Accept, Access-Control-Request-Method,Access-Control-Request-Headers, Authorization",
        },
      });
      console.log("Getting the data");
      console.log(res["data"]);
      setlatestBlock(res["data"])
    };
  };
  useEffect(testGetFun(), []);

  const getDateFunc = (x)=>{
      console.log(x);
      let d = new Date(x * 100);
    //   let dt = d.setUTCSeconds(x)
      console.log("This is the date");
      console.log(d)
      return d;
  }
  return (
<>
{latestBlock !== null?
<h1>{latestBlock["time"]}</h1>:null
}
</>
  )};

export default Data;
