import React, { useState } from 'react'
import Button from '@material-ui/core/Button';

const Singleblock = (props) => {
    const [blockData, setBlockData] = useState(null);
    const [input, setInput] = useState(null);

    const getSingleData = (event)=>{
        event.preventDefault();
        console.log("Clicked on Single data");
    }
    return (
        <>
            <div className="container">
            <form type="submit" onSubmit={getSingleData}>
                        <input type="text" name="todo" id="todo" value={input} placeholder="Enter the block hash" onChange={(event) => setInput(event.target.value)} />
                            <Button type="submit" className="addBtn" color="primary" onClick={getSingleData}>
                            Click
                            </Button>
                    </form>
            </div>
        </>
    )
}

export default Singleblock;
